DECLARE
v_data CLOB := (:1);
 e VARCHAR2(1);
 e1  VARCHAR2(2000);
BEGIN
  Delete ak_loader_temp;  
  execute immediate('alter session set cursor_sharing=SIMILAR');
DBMS_APPLICATION_INFO.SET_CLIENT_INFO (client_info=>'LILY-NEW-CONC1');
dbms_session.set_identifier('LILY-NEW-CONC1');
  for load_rec in ( 
    WITH load_data AS (select xt.* 
    from XMLTABLE ('/ROWSET/ENTRY'
    passing XMLTYPE (v_data) -- ****** We could pass this in as a bind variable, see Second Option above ******
    COLUMNS p_sql     VARCHAR2(4000) PATH 'X_SQL/text()',
          p_type        VARCHAR2(1) PATH 'X_TYPE',
          p_row      NUMBER PATH 'V_ROW',
          p_vendor_id        NUMBER PATH 'V_VENDOR_ID',
          p_invoice_num   VARCHAR2(500) PATH 'V_INVOICE_NUMBER',
          p_project_num   VARCHAR2(500) PATH 'V_PROJECT_NUMBER',
          p_task_num   VARCHAR2(500) PATH 'V_TASK_NUMBER',
          p_award_num   VARCHAR2(500) PATH 'V_AWARD_NUMBER',
          p_project_id     NUMBER PATH 'V_PROJECT_ID',
          p_task_id     NUMBER PATH 'V_TASK_ID',
          p_expenditure_item_date NUMBER PATH 'V_EXPENDITURE_ITEM_DATE',
          p_expenditure_type   VARCHAR2(2000) PATH 'V_EXPENDITURE_TYPE',
          p_expenditure_org_id NUMBER PATH 'V_EXPENDITURE_ORGANIZATION_ID',
          p_expenditure_org   VARCHAR2(2000) PATH 'V_EXPENDITURE_ORGANIZATION',
          p_org_id     NUMBER PATH 'V_ORG_ID',
          p_val_only   VARCHAR2(9) PATH 'V_VAL_ONLY',
          p_site_id     NUMBER PATH 'V_SITE_ID',
          p_site_specific_check VARCHAR2(1) PATH 'V_SITE_SPECIFIC_CHECK' 
          ) xt )
          select * from load_data) LOOP
             dbms_output.put_line ('invoice number is '||load_rec.p_invoice_num);

             BEGIN 
             m4aps_payinvwizard.load_line(x_sql => load_rec.p_sql, x_type => load_rec.p_type, v_row => load_rec.p_row, v_vendor_id => load_rec.p_vendor_id, v_invoice_number => load_rec.p_invoice_num, 
               v_project_number => load_rec.p_project_num, v_task_number => load_rec.p_task_num, v_award_number => load_rec.p_award_num,v_project_id => load_rec.p_project_id,
               v_task_id => load_rec.p_task_id, v_expenditure_item_date =>load_rec.p_expenditure_item_date, v_expenditure_type => load_rec.p_expenditure_type, 
               v_expenditure_organization_id => load_rec.p_expenditure_org_id, v_expenditure_organization => load_rec.p_expenditure_org, v_org_id => load_rec.p_org_id, 
               v_val_only => CASE load_rec.p_val_only WHEN 'true' THEN true ELSE false END, v_site_id => load_rec.p_site_id, v_site_specific_check => load_rec.p_site_specific_check, x_error => e);
             EXCEPTION
           WHEN OTHERS THEN
            e1 := replace(to_char(sqlerrm), ':', ';') || ':';
            INSERT INTO ak_loader_temp (tbl_index,line_content,session_id
            ) VALUES (
                - ad_sessions_s.NEXTVAL,
                CASE load_rec.p_type WHEN 'I' THEN 'W' ELSE 'V' END||load_rec.p_row||'.' || e1,
                - ad_sessions_s.NEXTVAL
            ); 
            END;
  END LOOP;
  execute immediate('alter session set cursor_sharing=EXACT');
END;
